import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import {ReactComponent as CogIcon} from './icons/cog.svg'
import { ReactComponent as ChevronIcon } from './icons/chevron.svg';


export default function HomePage() {
    const [showItem, setShowItem] = useState(false);
    const menuClickHandler = (param) => {
        if (showItem === param) {
          setShowItem(null);
        } else {
          setShowItem(param);
        }
      };    
    return (
        <div>
        <Navbar bg="dark" variant="dark">
            <NavItem icon = "Home"/>
            <NavItem icon = "Education" onClick={() => menuClickHandler("Edu")}>
            <DropdownMenuEducation></DropdownMenuEducation> 
            </NavItem>  
            <NavItem icon = "Personal Finances" onClick={() => menuClickHandler("Fin")}>
            <DropdownMenuFinance></DropdownMenuFinance>
            </NavItem>
        </Navbar>
        </div>


    //     <div className="text-center">
    //         <h1 className="main-title home-page-title">Welcome Back Spencer!</h1>
    //         <Link to="/">
    //             <button className="primary-button">Log out</button>
    //         </Link>
    //     </div>
     );
}

function Navbar(props) {
    return (
      <nav className="navbar">
        <ul className="navbar-nav">{props.children}</ul>
      </nav>
    );
  }


function NavItem(props){
    const [open, setOpen] = useState(false); // sets up the inital state to be false
    return(
        <li className='nav-item'>
            <a href='#' className='icon-button' onClick={() => setOpen(!open)}>  
                {props.icon}
            </a>
            {open && props.children}
        </li>
    )
}

function DropdownMenuEducation(){
    const [activeMenu, setActiveMenu] = useState('main'); //use state to reference which dropdown we want to refer to
    // const [menuHeight, setMenuHeight] = useState('null');
    // const dropdownRef = useRef(null);

    // useEffect(()=>{
    //     setMenuHeight(dropdownRef.current?.firstChild.offsetHeight)
    // },[])

    // function calcHeight(el){
    //     const height =el.offsetHeight;
    //     setMenuHeight(height);
    // }

    function DropdownItem(props){
        return(
            <a href='#' className='menu-item' onClick={() => props.goToMenu && setActiveMenu()}>
                <span className='icon-button-dropdown'> {props.leftIcon}</span>
                {props.children}
                <span className='icon-right'>{props.rightIcon}</span>
            </a>
        )
    }

    return(
        <div className='dropdown'>

           <div className='menu'>
            <DropdownItem leftIcon={<CogIcon />} icon-right={<ChevronIcon />} goToMenu = "About">My profile
            </DropdownItem>
            <DropdownItem >About</DropdownItem>
            </div>

        </div>
    )

    //Additional next time if we want to setup subset dropdown


    // return(
    //     <div className='dropdown'>
    //         <CSSTransition 
    //         in={activeMenu == 'main'} 
    //         unmountOnExit 
    //         timeout = {500}
    //         classNames="menu-primary"
    //         >
    //        <div className='menu'>
    //         <DropdownItem leftIcon={<CogIcon />} icon-right={<ChevronIcon />} goToMenu = "About">My profile
    //         </DropdownItem>
    //         <DropdownItem >About</DropdownItem>
    //         </div>
    //         </CSSTransition>

    //         <CSSTransition 
    //         in={activeMenu == 'settings'} 
    //         unmountOnExit 
    //         timeout = {500}
    //         classNames="menu-secondary"
    //         >
    //        <div className='menu'>
    //         <DropdownItem leftIcon={<CogIcon />} icon-right={<ChevronIcon />}goToMenu = "My profile">My profile
    //         </DropdownItem>
    //         <DropdownItem >About</DropdownItem>
    //         </div>
    //         </CSSTransition>
    //     </div>
    // )

}

function DropdownMenuFinance(){
    const [activeMenu, setActiveMenu] = useState('secondary'); //use state to reference which dropdown we want to refer to

    function DropdownItem(props){
        return(
            <a href='#' className='menu-item' onClick={() => props.goToMenu && setActiveMenu()}>
                <span className='icon-button-dropdown'> {props.leftIcon}</span>
                {props.children}
                <span className='icon-right'>{props.rightIcon}</span>
            </a>
        )
    }

    return(
        <div className='dropdown'>

           <div className='menu'>
            <DropdownItem leftIcon={<CogIcon />} icon-right={<ChevronIcon />} goToMenu = "About">My profile
            </DropdownItem>
            <DropdownItem >About</DropdownItem>
            </div>

        </div>
    )
}
