import { Link } from 'react-router-dom'




export default function HomePage() {
    return (
        <div>
        <Navbar bg="dark" variant="dark">
            <NavItem icon = "Home"/>
            <NavItem icon = "Education"/>
            <NavItem icon = "Finances"/>
            <NavItem icon={"Subset"}>
                <DropdownMenu> </DropdownMenu> 
