import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import {ReactComponent as CogIcon} from './icons/cog.svg'
import { ReactComponent as ChevronIcon } from './icons/chevron.svg';

import { CSSTransition } from 'react-transition-group';


export default function HomePage() {
    return (
        <div>
        <Navbar bg="dark" variant="dark">
            <NavItem icon = "Home"/>
            <NavItem icon = "Education"/>   
            <NavItem icon = "Personal Finances">
            <DropdownMenu></DropdownMenu>
            </NavItem>
            <NavItem icon={"Subset"}>
                <DropdownMenu> </DropdownMenu> 
            </NavItem>
        </Navbar>
        </div>


    //     <div className="text-center">
    //         <h1 className="main-title home-page-title">Welcome Back Spencer!</h1>
    //         <Link to="/">
    //             <button className="primary-button">Log out</button>
    //         </Link>
    //     </div>
     );
}

function Navbar(props) {
    return (
      <nav className="navbar">
        <ul className="navbar-nav">{props.children}</ul>
      </nav>
    );
  }


function NavItem(props){
    const [open, setOpen] = useState(false); // sets up the inital state to be false
    return(
        <li className='nav-item'>
            <a href='#' className='icon-button' onClick={() => setOpen(!open)}>  
                {props.icon}
            </a>
            {open && props.children}
        </li>
    )
}

function DropdownMenu(){
    const [activeMenu, setActiveMenu] = useState('main'); //use state to reference which dropdown we want to refer to
    // const [menuHeight, setMenuHeight] = useState('null');
    // const dropdownRef = useRef(null);

    // useEffect(()=>{
    //     setMenuHeight(dropdownRef.current?.firstChild.offsetHeight)
    // },[])

    // function calcHeight(el){
    //     const height =el.offsetHeight;
    //     setMenuHeight(height);
    // }

    function DropdownItem(props){
        return(
            <a href='#' className='menu-item'>
                <span className='icon-button-dropdown'> {props.leftIcon}</span>
                {props.children}
                <span className='icon-right'>{props.rightIcon}</span>
            </a>
        )
    }

    return(
        <div className='dropdown'>
            <CSSTransition 
            in={activeMenu == 'main'} 
            unmountOnExit 
            timeout = {500}
            classNames="menu-primary"
            >
           <div className='menu'>
            <DropdownItem leftIcon={<CogIcon />} icon-right={<ChevronIcon />}goToMenu = "Spencer">My profile
            </DropdownItem>
            </div>
            </CSSTransition>

            <CSSTransition 
            in={activeMenu == 'settings'} 
            unmountOnExit 
            timeout = {500}
            classNames="menu-secondary"
            >
           <div className='menu'>
            <DropdownItem leftIcon={<CogIcon />} icon-right={<ChevronIcon />}goToMenu = "Spencer">My profile
            </DropdownItem>
            </div>
            </CSSTransition>
        </div>
    )

}
